// Angular Import
import { Component, OnInit } from '@angular/core';

// Cdk Drag and Drop Import
import { CdkDragDrop, moveItemInArray, transferArrayItem } from '@angular/cdk/drag-drop';

// Model Import
import { Taskboard } from '../_model/taskboard';



@Component({
  selector: 'app-task-board',
  templateUrl: './task-board.component.html',
  styleUrls: ['./task-board.component.scss']
})
export class TaskBoardComponent implements OnInit {

  // Holds the list of Backlog Items
  public backlogItems: Taskboard[] = [];
  public todoItems: Taskboard[] = [
    {
      name: 'Create Footer',
      status: 'Backlog',
      totalHours: 12,
      remainingHours: 12,
      type: 'Task',
      priority: 'Med Priority',
      commentCount: 4,
      attachmentCount: 2,
      profile: 'data:image/jpeg;base64,',
    },
    {
      name: 'Landing page UI',
      status: 'Backlog',
      totalHours: 24,
      remainingHours: 24,
      type: 'Task',
      priority: 'High Priority',
      commentCount: 4,
      attachmentCount: 2,
      profile: 'data:image/jpeg;base64,',
    }
  ];
  public inProgessItems: Taskboard[] = [];
  public completedItems: Taskboard[] = [{
    name: 'Create Taskboard',
    status: 'Completed',
    totalHours: 8,
    remainingHours: 8,
    type: 'Task',
    priority: 'High Priority',
    commentCount: 4,
    attachmentCount: 2,
    profile: 'data:image/jpeg;base64,',
  }];

  public statusObject = {
    'cdk-drop-list-1': 'Todo',
    'cdk-drop-list-2': 'In Progress',
    'cdk-drop-list-3': 'Completed',
  };


  constructor() { }

  public ngOnInit(): void {
    this.intializeBacklogItems();
  }

  // Initializing the backlog Items
  public intializeBacklogItems(): void {
    this.backlogItems = [
      {
        name: 'Create Dashboard',
        status: 'Backlog',
        totalHours: 8,
        remainingHours: 8,
        type: 'Task',
        priority: 'High Priority',
        commentCount: 4,
        attachmentCount: 2,
        profile: 'data:image/jpeg;base64,',
      },
      {
        name: 'Create Login Page',
        status: 'Backlog',
        totalHours: 12,
        remainingHours: 12,
        type: 'Task',
        priority: 'High Priority',
        commentCount: 4,
        attachmentCount: 2,
        profile: 'data:image/jpeg;base64,',
      },
      {
        name: 'Fix Header Issue',
        status: 'Backlog',
        totalHours: 12,
        remainingHours: 12,
        type: 'Bug',
        priority: 'Med Priority',
        commentCount: 4,
        attachmentCount: 2,
        profile: 'data:image/jpeg;base64,',
      }
    ];
  }

  // backlog Items
  public dropItems(event: CdkDragDrop<Taskboard[]>): void {
    if (event.previousContainer === event.container) {
      moveItemInArray(event.container.data, event.previousIndex, event.currentIndex);
    } else {
      if (event.previousContainer.data.length > 0) {
        const movingItem = event.previousContainer.data.find((d) => ['In Progress', 'Completed'].includes(d.status));
        if (movingItem && Object.keys(movingItem).length > 0) {
          // Completed task Cannot be moved
          if (movingItem.status === 'Completed') {
            return;
          }

          // if a task in In Progress Status It cannot moved to Backlog
          if (movingItem.status === 'In Progress' && event.container.id === 'cdk-drop-list-0') {
            return;
          }
        }
        this.transferItems(event);
      } else {
        this.transferItems(event);
      }

    }
  }

  public transferItems(event: CdkDragDrop<Taskboard[]>): void {
    const movingItem = event.previousContainer.data[event.previousIndex];
    movingItem.status = this.statusObject[event.container.id];
    transferArrayItem(
      event.previousContainer.data, event.container.data,
      event.previousIndex, event.currentIndex);
  }

}
